import React, {Fragment } from 'react';
import '../../App.css'
import '../../assets/css/bootstrap.min.css'
import '../../assets/css/now-ui-kit.css?v=1.3.0'
import '../../assets/demo/demo.css'
import {Tab,Row,Col,ListGroup} from 'react-bootstrap';
// import MenuIcon from '@material-ui/icons/Menu';
// import AccountCircleIcon from '@material-ui/icons/AccountCircle';
// import AnnouncementIcon from '@material-ui/icons/Announcement';
// import ContactlessIcon from '@material-ui/icons/Contactless';
// import ListAltIcon from '@material-ui/icons/ListAlt';
// import LanguageIcon from '@material-ui/icons/Language';
// import DnsIcon from '@material-ui/icons/Dns';
// import Collapse from '@material-ui/core/Collapse';
// import ExpandLess from '@material-ui/icons/ExpandLess';
// import ExpandMore from '@material-ui/icons/ExpandMore';
// import DehazeIcon from '@material-ui/icons/Dehaze';
import List from '@material-ui/core/List';
import ListItem from '@material-ui/core/ListItem';
import DashboardIcon from '@material-ui/icons/Dashboard';
import ShoppingBasketIcon from '@material-ui/icons/ShoppingBasket';
import '../../assets/css/navVertical.css'

function NavVertical() {

  let user=JSON.parse(localStorage.getItem('user-info'))
  const id=user.id;
  const [open, setOpen] = React.useState(false);
  const handleClick = () => {
    setOpen(!open);
  };

  return (



    <Fragment>
    <div className="navVerticalPC" style={{width:'20%'}}>
    <Tab.Container  >
      <Row style={{width:"106%",margin:'0px 0px 0px -15px'}}>
        <Col style={{padding:"0px"}}>
          <ListGroup>
            <ListGroup.Item action href="/servicePrincipale/tableauDeBord">
            <div className='flex'><div ><DashboardIcon className='iconNavVertical'/></div><div> Tableau de bord</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href={"/servicePrincipale/ProfilService/"+id}>
              <div className='flex'><div ><AccountCircleIcon className='iconNavVertical'/></div><div> Profil Service</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href={"/servicePrincipale/demandes/"+id}>
              <div className='flex'><div><DnsIcon className='iconNavVertical'/></div><div> Demande Appels Offre</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href="/servicePrincipale/mesAppelsOffres">
              <div className='flex'><div><MenuIcon className='iconNavVertical'/></div><div> Mes Appels d'offres</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href="/servicePrincipale/litiges">
              <div className='flex'><div><AnnouncementIcon className='iconNavVertical'/></div><div> Litiges</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href="/servicePrincipale/facture">
              <div className='flex'><div><ContactlessIcon className='iconNavVertical'/></div><div> Factures / Paiements</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href="/servicePrincipale/produits">
              <div className='flex'><div><ListAltIcon className='iconNavVertical'/></div><div> Produits</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href="/servicePrincipale/achats">
              <div className='flex'><div><ShoppingBasketIcon className='iconNavVertical'/></div><div>Mes Commandes</div></div>
            </ListGroup.Item>
            <ListGroup.Item action href="https://www.kertys.com/">
              <div className='flex'><div><LanguageIcon className='iconNavVertical'/></div><div> https://www.kertys.com/</div></div>
            </ListGroup.Item>
          </ListGroup>
        </Col>

      </Row>
    </Tab.Container>
    </div>

    <div className='iconNavVertical' style={{fontSize:'15px',backgroundColor:'#e5e5e5'}}>
    <ListItem button onClick={handleClick}>
      <DehazeIcon style={{marginLeft:'3%'}}/>
      {open ? <ExpandLess /> : <ExpandMore />}
    </ListItem>
    <Collapse in={open} timeout="auto" unmountOnExit>
      <List component="div" disablePadding>
        <ListGroup.Item action href="/servicePrincipale/tableauDeBord" style={{backgroundColor:'#e5e5e5'}}>
        <div className='flex'><div ><MenuIcon className='iconNavVertical'/></div><div> Tableau de bord</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href={"/servicePrincipale/profilFournisseur/"+id} style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div ><AccountCircleIcon className='iconNavVertical'/></div><div> Profil Fournisseur</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href="/servicePrincipale/demandes" style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div><DnsIcon className='iconNavVertical'/></div><div> Demande Appels Offre</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href="/servicePrincipale/mesAppelsOffres" style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div><MenuIcon className='iconNavVertical'/></div><div> Mes Appels d'offres</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href="/servicePrincipale/litiges" style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div><AnnouncementIcon className='iconNavVertical'/></div><div> Litiges</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href="/servicePrincipale/facture" style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div><ContactlessIcon className='iconNavVertical'/></div><div> Factures / Paiements</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href="/servicePrincipale/produits" style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div><ListAltIcon className='iconNavVertical'/></div><div> Produits</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href="/servicePrincipale/achats" style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div><ShoppingBasketIcon className='iconNavVertical'/></div><div> Mes Commandes</div></div>
        </ListGroup.Item>
        <ListGroup.Item action href="https://www.kertys.com/" style={{backgroundColor:'#e5e5e5'}}>
          <div className='flex'><div><LanguageIcon className='iconNavVertical'/></div><div> https://www.kertys.com/</div></div>
        </ListGroup.Item>
      </List>
    </Collapse>
    </div>
    </Fragment>
);
}

export default NavVertical;
