import React, { useState,Fragment,useEffect } from 'react';
import '../css/Profil.css'
import NavVertical from '../../navVertical'
import NavBar from '../../navBar'
import Title from '../../dashboard/title'
import Dashboard from "../../dashboard/dashboard";
import BarChartIcon from '@mui/icons-material/BarChart';
import Fab from '@mui/material/Fab';
import EditIcon from '@mui/icons-material/Edit';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';
import PersonIcon from '@material-ui/icons/Person';

function Profil() {
  const [userprofile , setUserprofile] = useState('https://upload.wikimedia.org/wikipedia/commons/9/99/Sample_User_Icon.png')
  const [Info,setInfo]=useState([]);
  const [Cards,SetCards]=useState([
    { title:'Manucure',desc:'Pour de belles mains',image:require ('./assets/manicure.jpg'),key:'1' },
    { title:'Pedicure',desc:'Les pieds les plus attrayants',image:require ('./assets/pedicure.jpeg'),key:'2' },
    { title:'Epilation',desc:'Enlever, temporairement ou définitivement, les poils de la peau humaine, masculine ou féminine. ',image:require ('./assets/epilation.jpg'),key:'3' },
    { title:'Soins de corps ',desc:'proposent un massage pour délasser le corps',image:require ('./assets/soins-corps.jpg'),key:'4' },
    { title:'Soins de visage',desc:"Préserver la propreté du visage, à l'embellir, le raffermir, le tonifier ou le régénérer",image:require ('./assets/soin-visage.jpg'),key:'5' },
  ]);

  function imageHandler(e){
    const reader = new FileReader();
    reader.onload = () => {
      if(reader.readyState === 2){
        setUserprofile(reader.result)
      }
    }
    reader.readAsDataURL(e.target.files[0])
  }

  const displayUsers = Cards
.slice(pagesVisited, pagesVisited + usersPerPage)
.map((val,index,i=0) => {
  return (
      <div className="maincontainer3" style={{width:'85%',height:'88%'}} key={index}>
          <div className="thecard2" >
                <div className=" thefront2 flex" >
                     <div className="img-cardServicetitlesService "><img src={logoKertys} alt="" width="230px" height="210px" /></div>
                        <div className="card-titlesService3">
                          <div style={{fontSize: '20px'}}>{val.object}</div>
                          <div> Direction : {val.direction} </div>
                          <div style={{display: 'flex'}}>
                            <Button variant="contained" style={{background:'#2a9d8f', color:'white', width:'95px',fontSize:'11px'}}  className={classes.button} >
                              Offre : {val.dateOffre}
                            </Button>
                            <Button variant="contained" style={{background:'#2a9d8f', color:'white', width:'95px',fontSize:'11px'}}  className={classes.button} >
                              Limite : {val.dateLimite}
                            </Button>
                            <Button variant="contained" style={{background:'#f96332', color:'white', width:'95px',fontSize:'11px'}}  className={classes.button} >
                              {val.estimation} DH
                            </Button>
                          </div>
                          <div style={{display: 'flex'}}>

                          <Button variant="contained" style={{background:'grey', color:'white', width:'120px',fontSize:'10.5px'}}  className={classes.button} >
                            Date Demande {val.date}
                          </Button>
                          <Button variant="contained" style={{background:ColorEtat(val.accordAdmin)[0], color:ColorEtat(val.accordAdmin)[1], width:'95px',fontSize:'11px'}}  className={classes.button} >{etat(val.accordAdmin,"Doc Admin")}</Button>
                          <Button variant="contained" style={{background:ColorEtat(val.accordFinance)[0], color:ColorEtat(val.accordFinance)[1], width:'95px',fontSize:'11px'}}  className={classes.button} >{etat(val.accordFinance,"Doc Tech")}</Button>
                          <Button variant="contained" style={{background:ColorEtat(val.accordTech)[0], color:ColorEtat(val.accordTech)[1], width:'95px',fontSize:'11px'}}  className={classes.button} >{etat(val.accordTech,"Doc Finance")}</Button>
                          <Fab style={{margin:'10px 0px 0px 10px',display:appelOffre[index].accord===1?'':'none'}} size="small" color="default" onClick={()=>Contrat(index+pagesVisited)}><ContentPasteIcon/></Fab>
                          </div>
                        </div>


                   </div>
            </div>




      </div>

     );
  }
);

  return (
    <div className="App">
    <div style={{width:'100%'}}>
      <NavBar/>
      <div className="Paper1">
        <div className="TitlePaper">
          Dashboard<br/>
          <label className="TitlePaper2">Dashboard/Statistiques Utilisateurs</label>
        </div>
        <BarChartIcon style={{fontSize:'30px'}}/>
      </div>



      <div style={{width:'30%'}}>
        <div className='nom_client'>
          <div className='ProfilImg'>
            <img src={userprofile} style={{borderRadius: "7%",width:'150px'}} alt=''  />
          </div>
          <div className='nameProfil' >
            <span>{Info.nom}</span> <br/>
            <input type="file" name="file" id="file" className="inputfile" accept="image/*" onChange={imageHandler} />
            <center><Fab htmlFor="file" className='' size="small">
              <label style={{padding:'5% 0 0 10px ',borderRadius:'10px',width:'100%',height:'100%',backgroundColor:'transparent'}} htmlFor="file" className='profil_changee'><EditIcon/></label>
            </Fab></center>
          </div>
        </div>

        <div style={{padding:'0%',height:'30%'}}>
          <TextField size="small" style={{width:'43%',margin:'10% 0 0 0'}} id="outlined-basic" label="First Name" variant="outlined" />
          <TextField size="small" style={{width:'43%',margin:'10% 0 0 10%'}} id="outlined-basic" label="Last Name" variant="outlined" />
          <TextField size="small" style={{width:'96%',margin:'7% 0 0 0%'}} id="outlined-basic" label="Mail" variant="outlined" />
          <TextField size="small" style={{width:'96%',margin:'7% 0 0 0%'}} id="outlined-basic" label="Adresse" variant="outlined" />
          <TextField size="small" style={{width:'96%',margin:'7% 0 0 0%'}} id="outlined-basic" label="Phone Number" variant="outlined" />
      </div>



      <div className="gridOfCars">
                    <div className="rowa" style={{height:'100%'}}>
                        {displayUsers}
                        <ReactPaginate
                          previousLabel={"Previous"}
                          nextLabel={"Next"}
                          pageCount={pageCount}
                          onPageChange={changePage}
                          containerClassName={"paginationBttns"}
                          previousLinkClassName={"previousBttn"}
                          nextLinkClassName={"nextBttn"}
                          disabledClassName={"paginationDisabled"}
                          activeClassName={"paginationActive"}
                          />
                    </div>
                    <br/><br/>
                </div>








      </div>





    </div>


    </div>
  );
}

export default Profil;
