  import React, { useState } from "react";
import "./css/Menu.css";
import mylogo from "./img/logo2.png";
import { Link } from "react-router-dom";
import Modal from "react-modal";
import ModalLogin from "./Modal/ModalLogin";
import cancela from "./img/cancela.svg";
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogTitle from '@material-ui/core/DialogTitle';

function Menu() {
  const [Login, SetLogin] = useState(false);

  function HandleCloselogin(){
    SetLogin(false);
  }

  return (
    <nav className="poss">
      <div className="myMenu">
        <a className="pil pil_push" href="/">
          <img className="mylog" src={mylogo} width="80px" alt="" />
        </a>

        <div className="" id="navbarNav">
          <ul className="">
            <li className="">
              <Link className="pil" to="/">
                Home
              </Link>
            </li>
            <li className="">
              <a className="pil" href="/">
                Annonces
              </a>
            </li>
            <li className="">
              <a className="pil" href="/">
                About us
              </a>
            </li>
            <li className="">
              <a className="pil" href="/">
                Pricing
              </a>
            </li>
            <li className="Speciall">
              <button className="pil mycadr" onClick={() => SetLogin(true)}>
                Se connecter
              </button>

              <Dialog fullWidth={true} maxWidth={'md'} open={Login} onClose={HandleCloselogin} aria-labelledby="form-dialog-title" >
              <DialogTitle id="form-dialog-title">Ajouter Appel d'Offre</DialogTitle>
              <DialogContent>
              </DialogContent>
              <DialogActions>
                <Button variant="contained" style={{background:'red', color:'white'}} className={classes.button} startIcon={<CancelIcon />} onClick={HandleCloseLogin} >
                  cancel
                </Button>
                <Button variant="contained" style={{background:'blue', color:'white'}} className={classes.button} startIcon={<AddIcon />} >
                  Add
                </Button>
              </DialogActions>
            </Dialog>

            {/*<Modal
              isOpen={modalIsOpen}
              style={{
                overlay: {
                  backgroundColor: "rgba(205, 205, 205, 0.719)",
                },
                content: {
                  width: "330px",
                  height: "400px",
                  margin: "auto",
                },
              }}
            >
                <div>
                  <button
                    className="tocloose"
                    onClick={() => setModalOpen(false)}
                  >
                    <img src={cancela} alt="" width="17px" />{" "}
                  </button>
                </div>

                <ModalLogin />
              </Modal>*/}
              &nbsp;
              <Link className="pil mycadr" to="/Inscription">
                S'inscrire
              </Link>
            </li>

            <li>
              <a className="pil mycadr" href="/">
                <div className="moncadr">
                  <p className="contentCadr">Publier annonce</p>
                </div>
              </a>
            </li>
          </ul>
        </div>
      </div>
    </nav>
  );
}

export default Menu;
