import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import "../css/Profil.css";
import NavBar from "../../navBar";
import BarChartIcon from "@mui/icons-material/BarChart";
import Fab from "@mui/material/Fab";
import EditIcon from "@mui/icons-material/Edit";
import TextField from "@material-ui/core/TextField";
import ReactPaginate from "react-paginate";
import RoomIcon from "@mui/icons-material/Room";
import Avatar from "@mui/material/Avatar";
import BedOutlinedIcon from "@mui/icons-material/BedOutlined";
import BathroomOutlinedIcon from "@mui/icons-material/BathroomOutlined";
import BorderAllIcon from "@mui/icons-material/BorderAll";
import { pink } from "@mui/material/colors";
import Dialog from "@material-ui/core/Dialog";
import DialogActions from "@material-ui/core/DialogActions";
import DialogContent from "@material-ui/core/DialogContent";
import DialogTitle from "@material-ui/core/DialogTitle";

const useStyles = makeStyles((theme) => ({
  button: { margin: theme.spacing(1) },
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  input: {
    display: "none",
  },
}));

function Profil() {
  const [userprofile, setUserprofile] = useState(
    "https://upload.wikimedia.org/wikipedia/commons/9/99/Sample_User_Icon.png"
  );
  const [Info, setInfo] = useState([]);
  const [page, setPage] = React.useState(0);
  const classes = useStyles();
  const [pageNumber, setPageNumber] = useState(0);
  const usersPerPage = 2;
  const pagesVisited = pageNumber * usersPerPage;
  const [Cards, SetCards] = useState([
    {
      title: "Appartement en Location wilaya",
      prix: "1600",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "1",
      rooms: "3",
      surface: "100",
      adresse: "Sale",
      img: require("../../img/appt1.jpg").default,
      key: "1",
    },
    {
      title: "Appartement en Location (Par Mois) à Tétouan",
      prix: "1200",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "1",
      rooms: "2",
      surface: "80",
      adresse: "Rabat",
      img: require("../../img/appt2.jpg").default,
      key: "2",
    },
    {
      title: "Appart vide non meuble",
      prix: "1000",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "1",
      rooms: "2",
      surface: "50",
      adresse: "France,Tetouan",
      img: require("../../img/appt3.jpeg").default,
      key: "3",
    },
    {
      title: "Appartement meublée ",
      prix: "3000",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "2",
      rooms: "4",
      surface: "120",
      adresse: "Tetouan",
      img: require("../../img/appt4.jpg").default,
      key: "4",
    },
  ]);
  const pageCount = Math.ceil(Cards.length / usersPerPage);
  const [EditAnnonceDialog, SetEditAnnonceDialog] = useState(false);

  const changePage = ({ selected }) => {
    setPageNumber(selected);
  };
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  function HandleCloseEditAnnonceDialog() {
    SetEditAnnonceDialog(false);
  }

  function EditAnnonce(){
    console.log(true);
    SetEditAnnonceDialog(true);
  }

  function imageHandler(e) {
    const reader = new FileReader();
    reader.onload = () => {
      if (reader.readyState === 2) {
        setUserprofile(reader.result);
      }
    };
    reader.readAsDataURL(e.target.files[0]);
  }


  const displayUsers = Cards.slice(
    pagesVisited,
    pagesVisited + usersPerPage
  ).map((val, index, i = 0) => {
    return (
      <div className="" key={index} style={{ display: "flex", padding: "2%" }}>
        <div className="">
          <img style={{ borderBottomLeftRadius: "5px", borderTopLeftRadius: "5px", }} src={val.img} alt="house" height="220px" width="300px" />
        </div>
        <div style={{ backgroundColor: "white", width: "100%", height: "max-content", minHeight: "220px", minHeight: "220px", border: "2px dotted #e0e1e5 ", borderLeft: "none", borderBottomRightRadius: "10px", borderTopRightRadius: "10px", padding: "12px", }} >
          <h2 style={{ textAlign: "left", fontSize: "1.375rem", marginBottom: "1em", fontFamily: "Lato", marginBottom: "12px", fontWeight: "600", color: "rgb(53, 53, 53)", }} >
            <b>{val.title}</b>
          </h2>
          <p style={{ textAlign: "start", color: "#666", marginRight: "8px" }}> {val.description} </p>
          <div style={{ display: "flex", float: "right" }}>
            <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
              <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BathroomOutlinedIcon /> </Avatar>
              {val.bath} bath
            </div>
            <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
              <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BedOutlinedIcon /> </Avatar>
              {val.rooms} rooms
            </div>
            <div style={{ display: "flex", width: "120px" }}>
              <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BorderAllIcon /> </Avatar>
              {val.surface} m2
            </div>
          </div>
          <br />
          <div style={{ textAlign: "start", maxWidth: "300px" }}>
            <panel style={{ color: "#666" }}>
              <RoomIcon style={{ fontSize: "19px" }} />
              <b>{val.adresse}</b>
            </panel>
          </div>
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <div style={{ color: "#666", marginTop: "4px" }}>
              {" "}
              <strong>
                Prix :<b>{val.prix} MAD</b>{" "}
              </strong>{" "}
            </div>
            <div>
              <Button onClick={EditAnnonce} variant="contained" size="small" style={{ borderRadius: "10px", marginRight: "10px", backgroundColor: "#679436", color: "white", }} > Modify </Button>
              <Button variant="contained" size="small" style={{ borderRadius: "10px", backgroundColor: "red", color: "white", }} > Delete </Button>
            </div>
          </div>
        </div>

        <Dialog maxWidth={'xl'} fullWidth={false} open={EditAnnonceDialog} onClose={HandleCloseEditAnnonceDialog} aria-labelledby="form-dialog-title" >
          <DialogTitle id="form-dialog-title">
            <h3><b>Modifier Annonce</b></h3>
            <h6 style={{ color: "#666" }}>Modifier les informations de vote annoces si vous l'avez mal ajouté.</h6>
          </DialogTitle>
          <DialogContent>
            <div style={{ display: "flex" }}>
              <div>
                <div>
                  <img style={{ borderBottomLeftRadius: "5px", borderTopLeftRadius: "5px", }} src={Cards[0].img} alt="house" height="220px" width="300px" />
                </div>
                <div>
                  <img style={{ padding:'8px',borderBottomLeftRadius: "5px", borderTopLeftRadius: "5px", }} src={Cards[0].img} alt="house" height="70px" width="95px" />
                  <img style={{ padding:'8px',borderBottomLeftRadius: "5px", borderTopLeftRadius: "5px", }} src={Cards[0].img} alt="house" height="70px" width="95px" />
                  <img style={{ padding:'8px',borderBottomLeftRadius: "5px", borderTopLeftRadius: "5px", }} src={Cards[0].img} alt="house" height="70px" width="95px" />
                </div>
              </div>
              <div style={{padding:'2%'}}>
                <TextField inputProps={{style: {fontSize: '1.375rem',fontWeight:'600'}}} id="outlined-basic" fontSize='150' label="Titre" fullWidth defaultValue={Cards[0].title} />
                <TextField id="outlined-basic" fontSize='150' label="description" fullWidth defaultValue={Cards[0].description} />

                <div style={{ display: "flex", float: "right" }}>
                  <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
                    <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BathroomOutlinedIcon /> </Avatar>
                    {val.bath} bath
                  </div>
                  <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
                    <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BedOutlinedIcon /> </Avatar>
                    {val.rooms} rooms
                  </div>
                  <div style={{ display: "flex", width: "120px" }}>
                    <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BorderAllIcon /> </Avatar>
                    {val.surface} m²
                  </div>
                </div>
                <br />
                <div style={{ textAlign: "start", maxWidth: "300px" }}>
                  <panel style={{ color: "#666" }}>
                    <RoomIcon style={{ fontSize: "19px" }} />
                    <b>{val.adresse}</b>
                  </panel>
                </div>
                <div style={{ display: "flex", justifyContent: "space-between" }}>
                  <div style={{ color: "#666", marginTop: "4px" }}>
                    {" "}
                    <strong>
                      Prix :<b>{val.prix} MAD</b>{" "}
                    </strong>{" "}
                  </div>

                </div>
              </div>


            </div>



            {/*<div className="">
              <img style={{ borderBottomLeftRadius: "5px", borderTopLeftRadius: "5px", }} src={val.img} alt="house" height="220px" width="300px" />
            </div>
            <div style={{ backgroundColor: "white", width: "100%", height: "max-content", minHeight: "220px", minHeight: "220px", border: "2px dotted #e0e1e5 ", borderLeft: "none", borderBottomRightRadius: "10px", borderTopRightRadius: "10px", padding: "12px", }} >
              <h2 style={{ textAlign: "left", fontSize: "1.375rem", marginBottom: "1em", fontFamily: "Lato", marginBottom: "12px", fontWeight: "600", color: "rgb(53, 53, 53)", }} >
                <b>{val.title}</b>
              </h2>
              <p style={{ textAlign: "start", color: "#666", marginRight: "8px" }}> {val.description} </p>
              <div style={{ display: "flex", float: "right" }}>
                <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
                  <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BathroomOutlinedIcon /> </Avatar>
                  {val.bath} bath
                </div>
                <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
                  <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BedOutlinedIcon /> </Avatar>
                  {val.rooms} rooms
                </div>
                <div style={{ display: "flex", width: "120px" }}>
                  <Avatar sx={{ width: 24, height: 24, bgcolor: "#05668d", marginRight: 1, }} variant="rounded" > <BorderAllIcon /> </Avatar>
                  {val.surface} m2
                </div>
              </div>
              <br />
              <div style={{ textAlign: "start", maxWidth: "300px" }}>
                <panel style={{ color: "#666" }}>
                  <RoomIcon style={{ fontSize: "19px" }} />
                  <b>{val.adresse}</b>
                </panel>
              </div>
              <div style={{ display: "flex", justifyContent: "space-between" }}>
                <div style={{ color: "#666", marginTop: "4px" }}>
                  {" "}
                  <strong>
                    Prix :<b>{val.prix} MAD</b>{" "}
                  </strong>{" "}
                </div>
                <div>
                  <Button onClick={EditAnnonce} variant="contained" size="small" style={{ borderRadius: "10px", marginRight: "10px", backgroundColor: "#679436", color: "white", }} > Modify </Button>
                  <Button variant="contained" size="small" style={{ borderRadius: "10px", backgroundColor: "red", color: "white", }} > Delete </Button>
                </div>
              </div>*/}


          </DialogContent>
        </Dialog>
      </div>
    );
  });

  return (
    <div className="App">
      <div style={{ width: "100%" }}>
        <NavBar />
        <div className="Paper1">
          <div className="TitlePaper">
            Dashboard
            <br />
            <label className="TitlePaper2">
              Dashboard/Statistiques Utilisateurs
            </label>
          </div>
          <BarChartIcon style={{ fontSize: "30px" }} />
        </div>

        <div style={{ display: "flex" }}>
          <div
            style={{ width: "35%", padding: "0 16", margin: "10px 2% 10px 4%" }}
          >
            <div className="ProfilImg">
              <img src={userprofile} style={{ borderRadius: "7%", width: "150px" }} alt="" />
            </div>
            <div className="nameProfil">
              <span>{Info.nom}</span> <br />
              <input type="file" name="file" id="file" className="inputfile" accept="image/*" onChange={imageHandler} />
              <center>
                <Fab htmlFor="file" className="" size="small">
                  <label style={{ padding: "5% 0 0 10px ", borderRadius: "10px", width: "100%", height: "100%", backgroundColor: "transparent", }} htmlFor="file" className="profil_changee" >
                    <EditIcon />
                  </label>
                </Fab>
              </center>
            </div>
            <div style={{ padding: "0%", height: "30%" }}>
              <TextField size="small" style={{ width: "43%", margin: "10% 0 0 0" }} id="outlined-basic" label="First Name" variant="outlined" />
              <TextField size="small" style={{ width: "43%", margin: "10% 0 0 10%" }} id="outlined-basic" label="Last Name" variant="outlined" />
              <TextField size="small" style={{ width: "96%", margin: "7% 0 0 0%" }} id="outlined-basic" label="Mail" variant="outlined" />
              <TextField size="small" style={{ width: "96%", margin: "7% 0 0 0%" }} id="outlined-basic" label="Adresse" variant="outlined" />
              <TextField size="small" style={{ width: "96%", margin: "7% 0 0 0%" }} id="outlined-basic" label="Phone Number" variant="outlined" />
            </div>
          </div>
          <div style={{ width: "100%", margin: "2% 3% 5% 0" }}>
            <div className="rowa" style={{ height: "100% , margin: 10px" }}>
              {displayUsers}
              <ReactPaginate
                previousLabel={"Previous"}
                nextLabel={"Next"}
                pageCount={pageCount}
                onPageChange={changePage}
                containerClassName={"paginationBttns"}
                previousLinkClassName={"previousBttn"}
                nextLinkClassName={"nextBttn"}
                disabledClassName={"paginationDisabled"}
                activeClassName={"paginationActive"}
              />
            </div>
          </div>
        </div>
      </div>
    </div>

  );

}

export default Profil;
