import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Button from "@material-ui/core/Button";
import "../css/Profil.css";
import NavBar from "../../navBar";
import BarChartIcon from "@mui/icons-material/BarChart";
import Fab from "@mui/material/Fab";
import EditIcon from "@mui/icons-material/Edit";
import TextField from "@material-ui/core/TextField";
import ReactPaginate from "react-paginate";
import RoomIcon from "@mui/icons-material/Room";
import Avatar from "@mui/material/Avatar";
import BedOutlinedIcon from "@mui/icons-material/BedOutlined";
import BathroomOutlinedIcon from "@mui/icons-material/BathroomOutlined";
import BorderAllIcon from "@mui/icons-material/BorderAll";
import { pink } from "@mui/material/colors";

const useStyles = makeStyles((theme) => ({
  button: { margin: theme.spacing(1) },
  root: {
    "& > *": {
      margin: theme.spacing(1),
    },
  },
  input: {
    display: "none",
  },
}));

function Profil() {
  const [userprofile, setUserprofile] = useState(
    "https://upload.wikimedia.org/wikipedia/commons/9/99/Sample_User_Icon.png"
  );
  const [Info, setInfo] = useState([]);
  const [page, setPage] = React.useState(0);
  const classes = useStyles();
  const [pageNumber, setPageNumber] = useState(0);
  const usersPerPage = 2;
  const pagesVisited = pageNumber * usersPerPage;
  const [Cards, SetCards] = useState([
    {
      title: "Appartement en Location wilaya",
      prix: "1600",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "1",
      rooms: "3",
      surface: "100",
      adresse: "Sale",
      img: require("../../img/appt1.jpg").default,
      key: "1",
    },
    {
      title: "Appartement en Location (Par Mois) à Tétouan",
      prix: "1200",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "1",
      rooms: "2",
      surface: "80",
      adresse: "Rabat",
      img: require("../../img/appt2.jpg").default,
      key: "2",
    },
    {
      title: "Appart vide non meuble",
      prix: "1000",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "1",
      rooms: "2",
      surface: "50",
      adresse: "France,Tetouan",
      img: require("../../img/appt3.jpeg").default,
      key: "3",
    },
    {
      title: "Appartement meublée ",
      prix: "3000",
      description:
        " Le Marche Etna House is a fully equipped villa with many 3 big rooms swimming pool terrace and lots of other facilities.",
      bath: "2",
      rooms: "4",
      surface: "120",
      adresse: "Tetouan",
      img: require("../../img/appt4.jpg").default,
      key: "4",
    },
  ]);
  const pageCount = Math.ceil(Cards.length / usersPerPage);
  const changePage = ({ selected }) => {
    setPageNumber(selected);
  };
  const handleChangePage = (event, newPage) => {
    setPage(newPage);
  };

  function imageHandler(e) {
    const reader = new FileReader();
    reader.onload = () => {
      if (reader.readyState === 2) {
        setUserprofile(reader.result);
      }
    };
    reader.readAsDataURL(e.target.files[0]);
  }
  // <img src={require('path/to/one.jpeg')} />
  // src={ImgAppt}
  const displayUsers = Cards.slice(
    pagesVisited,
    pagesVisited + usersPerPage
  ).map((val, index, i = 0) => {
    return (
      <div className="" key={index} style={{ display: "flex", padding: "2%" }}>
        <div className="">
          <img
            style={{
              borderBottomLeftRadius: "5px",
              borderTopLeftRadius: "5px",
            }}
            src={val.img}
            alt="house"
            height="220px"
            width="300px"
          />
        </div>
        <div
          style={{
            backgroundColor: "white",
            width: "100%",
            height: "max-content",
            minHeight: "220px",
            minHeight: "220px",
            border: "2px dotted #e0e1e5 ",
            borderLeft: "none",
            borderBottomRightRadius: "10px",
            borderTopRightRadius: "10px",
            padding: "12px",
          }}
        >
          <h2
            style={{
              textAlign: "left",
              fontSize: "1.375rem",
              marginBottom: "1em",
              fontFamily: "Lato",
              marginBottom: "12px",
              fontWeight: "600",
              color: "rgb(53, 53, 53)",
            }}
          >
            <b>{val.title}</b>
          </h2>
          <p style={{ textAlign: "start", color: "#666", marginRight: "8px" }}>
            {val.description}
          </p>
          <div style={{ display: "flex", float: "right" }}>
            <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
              <Avatar
                sx={{
                  width: 24,
                  height: 24,
                  bgcolor: "#05668d",
                  marginRight: 1,
                }}
                variant="rounded"
              >
                <BathroomOutlinedIcon />
              </Avatar>
              {val.bath} bath
            </div>
            <div style={{ display: "flex", marginRight: "5%", width: "120px" }}>
              <Avatar
                sx={{
                  width: 24,
                  height: 24,
                  bgcolor: "#05668d",
                  marginRight: 1,
                }}
                variant="rounded"
              >
                <BedOutlinedIcon />
              </Avatar>
              {val.rooms} rooms
            </div>
            <div style={{ display: "flex", width: "120px" }}>
              <Avatar
                sx={{
                  width: 24,
                  height: 24,
                  bgcolor: "#05668d",
                  marginRight: 1,
                }}
                variant="rounded"
              >
                <BorderAllIcon />
              </Avatar>
              {val.surface} m2
            </div>
          </div>
          <br />
          <div style={{ textAlign: "start", maxWidth: "300px" }}>
            <panel style={{ color: "#666" }}>
              <RoomIcon style={{ fontSize: "19px" }} />
              <b>{val.adresse}</b>
            </panel>
          </div>
          <div style={{ display: "flex", justifyContent: "space-between" }}>
            <div style={{ color: "#666", marginTop: "4px" }}>
              {" "}
              <strong>
                Prix :<b>{val.prix} MAD</b>{" "}
              </strong>{" "}
            </div>
            <div>
              <Button
                variant="contained"
                size="small"
                style={{
                  borderRadius: "10px",
                  marginRight: "10px",
                  backgroundColor: "#679436",
                  color: "white",
                }}
              >
                Modify
              </Button>
              <Button
                variant="contained"
                size="small"
                style={{
                  borderRadius: "10px",
                  backgroundColor: "red",
                  color: "white",
                }}
              >
                Delete
              </Button>
            </div>
          </div>
        </div>
      </div>
    );
  });

  return (
    <div className="App">
      <div style={{ width: "100%" }}>
        <NavBar />
        <div className="Paper1">
          <div className="TitlePaper">
            Dashboard
            <br />
            <label className="TitlePaper2">
              Dashboard/Statistiques Utilisateurs
            </label>
          </div>
          <BarChartIcon style={{ fontSize: "30px" }} />
        </div>

        <div style={{ display: "flex" }}>
          <div
            style={{ width: "35%", padding: "0 16", margin: "10px 2% 10px 4%" }}
          >
            <div className="ProfilImg">
              <img
                src={userprofile}
                style={{ borderRadius: "7%", width: "150px" }}
                alt=""
              />
            </div>
            <div className="nameProfil">
              <span>{Info.nom}</span> <br />
              <input
                type="file"
                name="file"
                id="file"
                className="inputfile"
                accept="image/*"
                onChange={imageHandler}
              />
              <center>
                <Fab htmlFor="file" className="" size="small">
                  <label
                    style={{
                      padding: "5% 0 0 10px ",
                      borderRadius: "10px",
                      width: "100%",
                      height: "100%",
                      backgroundColor: "transparent",
                    }}
                    htmlFor="file"
                    className="profil_changee"
                  >
                    <EditIcon />
                  </label>
                </Fab>
              </center>
            </div>
            <div style={{ padding: "0%", height: "30%" }}>
              <TextField
                size="small"
                style={{ width: "43%", margin: "10% 0 0 0" }}
                id="outlined-basic"
                label="First Name"
                variant="outlined"
              />
              <TextField
                size="small"
                style={{ width: "43%", margin: "10% 0 0 10%" }}
                id="outlined-basic"
                label="Last Name"
                variant="outlined"
              />
              <TextField
                size="small"
                style={{ width: "96%", margin: "7% 0 0 0%" }}
                id="outlined-basic"
                label="Mail"
                variant="outlined"
              />
              <TextField
                size="small"
                style={{ width: "96%", margin: "7% 0 0 0%" }}
                id="outlined-basic"
                label="Adresse"
                variant="outlined"
              />
              <TextField
                size="small"
                style={{ width: "96%", margin: "7% 0 0 0%" }}
                id="outlined-basic"
                label="Phone Number"
                variant="outlined"
              />
            </div>
          </div>
          <div style={{ width: "100%", margin: "2% 3% 5% 0" }}>
            <div className="rowa" style={{ height: "100% , margin: 10px" }}>
              {displayUsers}
              <ReactPaginate
                previousLabel={"Previous"}
                nextLabel={"Next"}
                pageCount={pageCount}
                onPageChange={changePage}
                containerClassName={"paginationBttns"}
                previousLinkClassName={"previousBttn"}
                nextLinkClassName={"nextBttn"}
                disabledClassName={"paginationDisabled"}
                activeClassName={"paginationActive"}
              />
            </div>
          </div>
        </div>
      </div>
    </div>

  );
  <Dialog
    style={{ marginLeft: "26%", maxWidth: "600px" }}
    fullWidth={false}
    open={SignUp}
    onClose={HandleCloseSignUp}
    aria-labelledby="form-dialog-title"
  >
    <DialogTitle id="form-dialog-title">
      <h3>
        <b>Register New Account</b>
      </h3>
      <h6 style={{ color: "#666" }}>
        Let's get you all set up so you can verify your personal account and
        begin setting up your profile.
      </h6>
    </DialogTitle>
    <DialogContent>
      <div style={{ display: "block" }}>
        <div>
          <TextField
            label="First Name"
            width="50%"
            style={{ margin: "0 0 7%  0px" }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  {" "}
                  <PersonIcon />{" "}
                </InputAdornment>
              ),
            }}
          />
          <TextField
            label="Last Name"
            width="50%"
            style={{ margin: "0 0 7%  13%" }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  {" "}
                  <PersonIcon />{" "}
                </InputAdornment>
              ),
            }}
          />

          <TextField
            label="Adresse"
            width="50%"
            style={{ margin: "0 0 7%  0%" }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  {" "}
                  <PersonIcon />{" "}
                </InputAdornment>
              ),
            }}
          />
          <TextField
            label="Phone Number"
            width="50%"
            style={{ margin: "0 0 7%  13%" }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  {" "}
                  <PersonIcon />{" "}
                </InputAdornment>
              ),
            }}
          />

          <div style={{ display: "flex" }}>
            <TextField
              label="Mail"
              width="50%"
              style={{ margin: "1% 0 7%  0%" }}
              InputProps={{
                startAdornment: (
                  <InputAdornment position="start">
                    {" "}
                    <PersonIcon />{" "}
                  </InputAdornment>
                ),
              }}
            />
            <div style={{ margin: "0 0 7%  13%" }}>
              <InputLabel
                htmlFor="standard-adornment-password"
                style={{ fontSize: "12px" }}
              >
                Password
              </InputLabel>
              <Input
                style={{ marginBottom: "35px" }}
                id="standard-adornment-password"
                type={values.showPassword ? "text" : "password"}
                value={values.password}
                onChange={handleChange("password")}
                endAdornment={
                  <InputAdornment position="end">
                    <IconButton
                      aria-label="toggle password visibility"
                      onClick={handleClickShowPassword}
                      onMouseDown={handleMouseDownPassword}
                    >
                      {values.showPassword ? (
                        <Visibility />
                      ) : (
                        <VisibilityOff />
                      )}
                    </IconButton>
                  </InputAdornment>
                }
              />
            </div>
          </div>
          <Button
            variant="contained"
            style={{
              backgroundColor: "#05668d",
              color: "white",
              width: "100%",
              height: "40px",
              marginLeft: "0%",
            }}
            startIcon={<ArrowForwardIosIcon />}
          >
            Create Account
          </Button>
        </div>
        <br />
        <h6 style={{ color: "#666" }}>
          Already have an account?
          <label className="joinNow" onClick={LoginPopUp}>
            <b style={{ color: "#05668d" }}>Sign In</b>
          </label>
        </h6>
      </div>
    </DialogContent>
  </Dialog>
}

export default Profil;
