import React, { useState,Fragment,useEffect } from 'react';
import '../css/Profil.css'
import NavVertical from '../../navVertical'
import NavBar from '../../navBar'
import Title from '../../dashboard/title'
import Dashboard from "../../dashboard/dashboard";
import BarChartIcon from '@mui/icons-material/BarChart';
import Fab from '@mui/material/Fab';
import EditIcon from '@mui/icons-material/Edit';
import TextField from '@material-ui/core/TextField';
import InputAdornment from '@material-ui/core/InputAdornment';
import PersonIcon from '@material-ui/icons/Person';

function Profil() {
  const [userprofile , setUserprofile] = useState('https://upload.wikimedia.org/wikipedia/commons/9/99/Sample_User_Icon.png')
  const [Info,setInfo]=useState([]);

  function imageHandler(e){
    const reader = new FileReader();
    reader.onload = () => {
      if(reader.readyState === 2){
        setUserprofile(reader.result)
      }
    }
    reader.readAsDataURL(e.target.files[0])
  }

  return (
    <div className="App">
    <div style={{width:'100%'}}>
      <NavBar/>
      <div className="Paper1">
        <div className="TitlePaper">
          Dashboard<br/>
          <label className="TitlePaper2">Dashboard/Statistiques Utilisateurs</label>
        </div>
        <BarChartIcon style={{fontSize:'30px'}}/>
      </div>



      <div style={{width:'30%'}}>
        <div className='nom_client'>
          <div className='ProfilImg'>
            <img src={userprofile} style={{borderRadius: "7%",width:'150px'}} alt=''  />
          </div>
          <div className='nameProfil' >
            <span>{Info.nom}</span> <br/>
            <input type="file" name="file" id="file" className="inputfile" accept="image/*" onChange={imageHandler} />
            <center><Fab htmlFor="file" className='' size="small">
              <label style={{padding:'2% 0 0 20px ',borderRadius:'10px',width:'100%',height:'100%',backgroundColor:'transparent'}} htmlFor="file" className='profil_changee'><EditIcon/></label>
            </Fab></center>
          </div>
        </div>

        <div style={{padding:'0%',height:'100%'}}>
          <TextField style={{width:'43%',margin:'10% 0 0 0'}} id="outlined-basic" label="First Name" variant="outlined" />
          <TextField style={{width:'43%',margin:'10% 0 0 10%'}} id="outlined-basic" label="Last Name" variant="outlined" />
          <TextField style={{width:'96%',margin:'7% 0 0 0%'}} id="outlined-basic" label="Mail" variant="outlined" />
          <TextField style={{width:'96%',margin:'7% 0 0 0%'}} id="outlined-basic" label="Adresse" variant="outlined" />
          <TextField style={{width:'96%',margin:'7% 0 0 0%'}} id="outlined-basic" label="Phone Number" variant="outlined" />


      </div>




      </div>





    </div>


    </div>
  );
}

export default Profil;
